import React from 'react'
import { NavLink } from 'react-router-dom'
import styled from 'styled-components'
import { Github, LinkedIn, Twitter } from '../components/AllSvgs'
import { DarkTheme } from '../components/Themes'


const Icons = styled.div`
display: flex;
flex-direction: column;
align-items: center;

position: fixed;
bottom: 0;
left: 2rem;


z-index: 3;

&>*:not(:last-child) {
    margin: 0.5rem 0;
}
`

const Line = styled.span`
width: 2px;
height: 8rem;
background-color: ${props => props.theme.text};
`

const SocialIcons = (props) => {
  return (
    
    <Icons>
        <div>
            <NavLink style={{color:'inherit'}} target="_blank" to={{pathname:"https://github.com/brunocarmena57"}}>
                <Github width={25} height={25} fill={props.theme ==="dark" ? DarkTheme.text : DarkTheme.body } />
            </NavLink>
        </div>

        <div>
            <NavLink style={{color:'inherit'}} target="_blank" to={{pathname:"https://twitter.com/Brunooo111"}}>
                <Twitter width={25} height={25} fill={props.theme ==="dark" ? DarkTheme.text : DarkTheme.body } />
            </NavLink>
        </div>

        <div>
            <NavLink style={{color:'inherit'}} target="_blank" to={{pathname:"https://www.linkedin.com/in/bruno-ricardo-carmena-a6a21a1b1"}}>
                <LinkedIn width={25} height={25} fill={props.theme ==="dark" ? DarkTheme.text : DarkTheme.body } />
            </NavLink>
        </div>

        <Line />
    </Icons>
  )
}

export default SocialIcons
