import styled, { keyframes } from 'styled-components'
import { motion } from 'framer-motion'
import { NavLink } from 'react-router-dom'
import { Suspense, useState, lazy } from 'react'


import Intro from './Intro'
import Loading from '../subComponents/Loading'
import { mediaQueries } from "./Themes";


import LogoComponent from '../subComponents/LogoComponent'
import PowerButton from '../subComponents/PowerButton'
import SocialIcons from '../subComponents/SocialIcons'
import { BigHead } from './AllSvgs'

const MainContainer = styled(motion.div)`
background: ${props => props.theme.body};
width: 100vw;
height: 100vh;
overflow: hidden;

position: relative;

h2,h3,h4,h5,h6 {
    font-family: 'Karla', sans-serif;
    font-weight: 500;
}
h2 {
  ${mediaQueries(40)`
    font-size:1.2em;

`};

  ${mediaQueries(30)`
    font-size:1em;

`};
}
`;


const Container = styled.div`
padding: 2rem;
`


const SKILLS = styled(NavLink)`
color: ${props => props.theme.text};
position: absolute;
top: 50%;
right: calc(1rem + 2vw);
transform: rotate(90deg) translate(-50%, -50%);
text-decoration: none;
z-index: 1;
`

const WORK = styled(NavLink)`
color: ${props => props.click ? props.theme.body : props.theme.text};
position: absolute;
top: 50%;
left: calc(1rem + 2vw);
transform: translate(-50%, -50%) rotate(-90deg) ;
text-decoration: none;
z-index: 1;
@media only screen and (max-width: 50em) {
  text-shadow: ${(props) => (props.click ? "0 0 4px #000" : "none")};
}
`;

const BottomBar = styled.div`
position: absolute;
bottom: 1rem;
left: 0;
right: 0;
width: 100%;

display: flex;
justify-content: space-evenly;
`

const ABOUT = styled(NavLink)`
color: ${props => props.click ? props.theme.body : props.theme.text};
text-decoration: none;
z-index: 1;
`

const Contact = styled(NavLink)`
color: ${props => props.theme.text};
text-decoration: none;
z-index: 1;
`
const rotate = keyframes`
from {
  transform: rotate(0);
}
to {
  transform: rotate(360deg);
}
`

const Center = styled.button`
position: absolute;
bottom: ${props => props.click ? '77%' :'30%' };
left: ${props => props.click ? '92%' :'50%' };
transform: translate(-50%, -50%);
border: none;
outline: none;
background-color: transparent;
cursor: pointer;

display: flex;
flex-direction: column;
justify-content: center;
align-items:center;
transition: all 1s ease;


&>:first-child {
  animation: ${rotate} infinite 1.5s linear;


}

@media only screen and (max-width: 50em) {
  top: ${(props) => (props.click ? "90%" : "50%")};
  left: ${(props) => (props.click ? "90%" : "50%")};
  width: ${(props) => (props.click ? "80px" : "150px")};
  height: ${(props) => (props.click ? "80px" : "150px")};
}
@media only screen and (max-width: 30em) {
  width: ${(props) => (props.click ? "40px" : "150px")};
  height: ${(props) => (props.click ? "40px" : "150px")};
}
`;

const DarkDiv = styled.div`
position: absolute;
top: 0;
background-color: #2CCB69;
bottom: 0;
right: 50%;
width: ${props => props.click ? '50%' : '0%'};
height: ${props => props.click ? '100%' : '0%'};
z-index: 1;
transition: height 0.5s ease, width 1s ease 0.5s;

${(props) =>
  props.click
    ? mediaQueries(50)`
     height: 50%;
    right:0;

width: 100%;
transition: width 0.5s ease, height 1s ease 0.5s;

`
      : mediaQueries(50)`
       height: 0;
  
  width: 0;
  `};
`;


const StyleBigHead = styled.div`
h2 {
  color: "#2CCB69",
  fontFamily: 'Karla',
  fontWeight: 800,
  fontSize: 19,

}
 

` 
const Main = () => {

  const [click, setClick] = useState(false);
  const [path, setpath] = useState("");

  const handleClick = () => setClick(!click);

  const moveY = {
    y: "-100%",
  };
  const moveX = {
    x: `${path === "work" ? "100%" : "-100%"}`,
  };
  const mq = window.matchMedia("(max-width: 50em)").matches;

  return (
    <Suspense fallback={<Loading />}>
    <MainContainer
    key="modal"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        exit={path === "about" || path === "skills" ? moveY : moveX}
        transition={{ duration: 0.5 }}
        backgroundcolor={'#2CCB69'}
        >
      <DarkDiv click={click} />
        <Container>
        <PowerButton />
       <LogoComponent theme={click ? 'dark' : 'light'} />
       {mq ? (
            <SocialIcons theme="light" />
          ) : (
            <SocialIcons theme={click ? "dark" : "light"} />
          )}

       <SocialIcons theme={click ? 'dark' : 'light'} />
       <Center click={click}>
       {mq ? (
        <BigHead onClick={()=> handleClick()}
         width={click ? 120 : 200}
          height={click ? 120 : 200}
           fill='currentColor' />
           ) : (
        <BigHead
        onClick={() => handleClick()}
                width={click ? 120 : 200}
                height={click ? 120 : 200}
                fill="currentColor" />
           )}
        <StyleBigHead><h2>Clique Aqui!</h2></StyleBigHead>
       </Center>

       

       <SKILLS to="/skills">
        <motion.h2
        onClick={() => setpath("skills")}
        initial={{
          y: -100,
          transition: { type:'spring', duration: 1.5, delay:1}
        }}
        animate={{
          y:0,
          transition: { type:'spring', duration: 1.5, delay:1}
        }}
        whileHover={{scale: 1.1}}
        whileTap={{scale: 0.9}}
        >
        Skills
        </motion.h2>
       </SKILLS>

       <WORK to="/Work" click={click}>
        <motion.h2
        onClick={() => setpath("work")}
        initial={{
          y: -100,
          transition: { type:'spring', duration: 1.5, delay:1}
        }}
        animate={{
          y:0,
          transition: { type:'spring', duration: 1.5, delay:1}
        }}
        whileHover={{scale: 1.1}}
        whileTap={{scale: 0.9}}
        >
          Projetos        
        </motion.h2>
       </WORK>

       <BottomBar>
          <ABOUT onClick={() => setClick(false)}
              click={mq ? +false : +click}
              to="/about"
              >
          <motion.h2 
          onClick={() => setpath("about")}
          initial={{
            y: 100,
            transition: { type:'spring', duration: 1.5, delay:1}
          }}
          animate={{
            y:0,
            transition: { type:'spring', duration: 1.5, delay:1}
          }}
          whileHover={{scale: 1.1}}
          whileTap={{scale: 0.9}}
          >
          Sobre mim
          </motion.h2>
          </ABOUT>
          <Contact click={+click} target="_blank" to={{pathname:"mailto:brunorcarmena1@gmail.com"}}>
        <motion.h2
        initial={{
          y: 100,
          transition: { type:'spring', duration: 1.5, delay:1}
        }}
        animate={{
          y:0,
          transition: { type:'spring', duration: 1.5, delay:1}
        }}
        whileHover={{scale: 1.1}}
        whileTap={{scale: 0.9}}
        >
          Contacte-se comigo!
        </motion.h2>
       </Contact>
       </BottomBar>


      



        </Container>
        {click ? <Intro click={click} /> : null}
     </MainContainer>
     </Suspense>
  );
};

export default Main;
