import React from 'react'
import { NavLink } from 'react-router-dom'
import styled from 'styled-components'
import LogoComponent from '../subComponents/LogoComponent'
import PowerButton from '../subComponents/PowerButton'
import SocialIcons from '../subComponents/SocialIcons'
import { BigHead } from './AllSvgs'


const MainContainer = styled.div`
background: ${props => props.theme.body};
width: 100vw;
height: 100vh;
overflow: hidden;

position: relative;

h2,h3,h4,h5,h6 {
    font-family: 'Karla', sans-serif;
    font-weight: 500;
}
`

const Container = styled.div`
padding: 2rem;
`

const Contact = styled(NavLink)`
color: ${props => props.theme.text};
position: absolute;
top: 2rem;
right: calc(1rem + 2vw);
text-decoration: none;
z-index: 3;
`

const SKILLS = styled(NavLink)`
color: ${props => props.theme.text};
position: absolute;
top: 50%;
right: calc(1rem + 2vw);
transform: rotate(90deg) translate(-50%, -50%);
text-decoration: none;
z-index: 1;
`

const WORK = styled(NavLink)`
color: ${props => props.theme.text};
position: absolute;
top: 50%;
left: calc(1rem + 2vw);
transform: translate(-50%, -50%) rotate(-90deg) ;
text-decoration: none;
z-index: 1;
`

const BottomBar = styled.div`
position: absolute;
bottom: 1rem;
left: 0;
right: 0;
width: 100%;

display: flex;
justify-content: space-evenly;
`

const ABOUT = styled(NavLink)`
color: ${props => props.theme.text};
text-decoration: none;
z-index: 1;
`

const Main = () => {
  return (
    <MainContainer>
        <Container>
        <PowerButton />
       <LogoComponent />
       <SocialIcons />

       <Center>
        <BigHead width={150} height={150} fill='currentColor' />
        <span>click here</span>
       </Center>

       <Contact target="_blank" to={{pathname:"mailto:brunorcarmena1@gmail.com"}}>
        <h2>
          Contacte-se comigo!
        </h2>
       </Contact>

       <SKILLS to="/skills">
        <h2>
        Minhas skills
        </h2>
       </SKILLS>

       <WORK to="/Work">
        <h2>
          Projetos        
        </h2>
       </WORK>

       <BottomBar>
          <ABOUT to="/about">
          <h2>
          Sobre mim
          </h2>
          </ABOUT>
       </BottomBar>


      



        </Container>
     </MainContainer>
  )
}

export default Main
