import React, { useEffect, useRef, lazy, Suspense } from 'react';
import styled, { ThemeProvider } from 'styled-components';
import { DarkTheme, mediaQueries } from './Themes';
import { motion } from 'framer-motion';

import { Work } from "../data/WorkData";
import Card from '../subComponents/Card';
import { BigHead } from './AllSvgs';
import BigTitle from '../subComponents/BigTitle';
import Loading from "../subComponents/Loading";

import LogoComponent from '../subComponents/LogoComponent';
import SocialIcons from '../subComponents/SocialIcons';
import PowerButton from '../subComponents/PowerButton';

const Box = styled(motion.div)`
background-color: ${props => props.theme.text};
height: 400vh;
position: relative;
display: flex;
align-items: center;

`

const Main = styled(motion.ul)`
position: fixed;
top: 12rem;
left: calc(10rem + 15vw);
height: 40vh;
display: flex;
color: #2CCB69;

${mediaQueries(50)`
        
        
left:calc(8rem + 15vw);

`};

${mediaQueries(40)`
top: 30%;

left:calc(6rem + 15vw);

`};

${mediaQueries(40)`

left:calc(2rem + 15vw);

`};
${mediaQueries(25)`

left:calc(1rem + 15vw);

`};
`;

const Rotate = styled.span`
display: block;
position: fixed;
right: 1rem;
bottom: 1rem;
width: 80px;
height: 80px;
z-index: 1;

z-index: 1;
${mediaQueries(40)`
   width:60px;
       height:60px;   
     svg{
       width:60px;
       height:60px;
     }

`};
${mediaQueries(25)`
      width:50px;
       height:50px;
      svg{
       width:50px;
       height:50px;
     }

`};
`;

// Animação para troca de tela
const container = {

  hidden: {opacity:0},
  show: {
    opacity:1,

    transition:{
      staggerChildren:0.5,
      duration:0.5,
    }
  }
}

const WorkPage = () => {

  const ref = useRef(null);
  const bighead = useRef(null);

useEffect(() => {
  let element = ref.current;

  const rotate = () => {
    element.style.transform = `translateX(${-window.pageYOffset}px)`

    return (bighead.current.style.transform = `rotate(` + -window.pageYOffset + 'deg)')
  }

  window.addEventListener('scroll', rotate)

  return () => window.removeEventListener('scroll', rotate)
}, [])


  return (
    <ThemeProvider theme={DarkTheme}>
       <Suspense fallback={<Loading />}>
<Box
key="work"
initial={{ opacity: 1 }}
animate={{ opacity: 1, transition: { duration: 1 } }}
exit={{ opacity: 1, transition: { duration: 0.5 } }}
>

  <LogoComponent theme='light' />
  <SocialIcons  theme='light'/>
  <PowerButton />
    

    <Main ref={ref} variants={container} initial='hidden' animate='show' >
     {
      Work.map( d =>
        <Card key={d.id} data={d} />
        )
     }
    </Main>

<Rotate ref={bighead}>
  <BigHead width={80} height={80} fill={DarkTheme.text} />
</Rotate>

<BigTitle text="PROJETOS" top='10%' right="20%" />
      </Box>
      </Suspense>
    </ThemeProvider>
    
  )
}

export default WorkPage
